mapper.insert:
INSERT INTO tb_saved_event (
event_id,
account_id
) VALUES (
#{eventId},
#{accountId}
)

mapper.delete:
DELETE FROM tb_saved_event
WHERE account_id = #{accountId}
    AND event_id = #{eventId}

mapper.select:
SELECT se.event_id, e.title, es.event_date, es.event_schedule_id, e.thumbnail_url
FROM tb_saved_event se JOIN tb_event e ON se.event_id = e.event_id JOIN tb_event_schedule es ON e.event_id = es.event_id
WHERE se.account_id = #{accountId}
LIMIT #{limit} OFFSET #{offset}
